import { NativeModules } from 'react-native';

type CryptoAesCbcType = {
  multiply(a: number, b: number): Promise<number>;
  encryptInHex(iv:String,secretKey:String,text:String,padding:String):Promise<String>;
  decryptByHex(iv:String,secretKey:String,text:String,padding:String):Promise<String>;
  encryptInBase64(iv:String,secretKey:String,text:String,padding:String):Promise<String>;
  decryptByBase64(iv:String,secretKey:String,text:String,padding:String):Promise<String>;
  sha256(text:String):Promise<String>;
};

const { CryptoAesCbc } = NativeModules;

export default CryptoAesCbc as CryptoAesCbcType;
